import React, { useEffect, useState } from 'react';
import {
    useParams, Link
} from "react-router-dom";
import './comun.css';
import './botones.css';
import './inicio.css';

function DetalleProducto(){
    let { idProducto } = useParams();
    const [producto, cambiarProducto] = useState([]);

    useEffect( () => {
        if(!idProducto) return <div>No hay productos</div>;

        Promise.all([
            fetch(`http://localhost:4000/obtProducto/?id=${idProducto}`, { method: "GET" })
        ])
        .then(promesaResuelta => {
            Promise.all(promesaResuelta.map(pR => pR.json()))
            .then(([resultadoProducto]) => {
                console.log(resultadoProducto)
                cambiarProducto(resultadoProducto.data[0]);
            })
            console.log(producto)
        })
        .catch(err => {
            return(
                <div>
                    No se encuentra el producto seleccionado. Vuelva a cargar la página
                </div>
            );
        });
    }, [idProducto]);

    return(
        <main id="main">
            <article id="article-index" className="article-grid">
                <div className="img-detalle">
                    <img src={producto.imagen} alt="Imagen producto" />
                </div>
                <div className="descripcionCorta-detalle">
                    <h2>{producto.descripcionCorta}</h2>
                </div>
                <div className="ubicacion">
                    <p>Nos puedes encontrar en nuestras dos sucursales:</p>
                    <ul>
                        <li>
                            Dirección: Juan Paullier 2378 entre Amézaga y Domingo Aramburú - Mapa: <Link to="/sucursal1"><button className="custom-btn btn-16"><i className="fas fa-map-marked-alt"></i></button></Link>
                        </li>
                        <li>
                            Dirección: Demóstenes 3532 - Mapa:  <Link to="/sucursal2"><button className="custom-btn btn-16"><i className="fas fa-map-marked-alt"></i></button></Link>
                        </li>
                    </ul>
                </div>
                <div className="divBotones">
                    <Link to="/tienda">
                        <button className="custom-btn btn-2 boton1">Ir a la tienda</button>
                    </Link>
                    <Link to="/login">
                        <button className="custom-btn btn-2 boton2">Iniciar Sesión</button>
                    </Link>
                    <Link to="/registro">
                        <button className="custom-btn btn-2 boton3">Registrarme</button>
                    </Link>
                </div>
            </article>
        </main>
    );
}

export default DetalleProducto;
