import React, { useState, useEffect } from "react";
//import './comun.css';
import './tienda.css';
import SectionArticulo from './sectionArticulo';

function Tienda(){
    const [destacados, cambiarDestacados] = useState([]);
    const [pcsEscritorio, cambiarPcsEscritorio] = useState([]);
    const [notebooks, cambiarNotebooks] = useState([]);
    const [consolas, cambiarConsolas] = useState([]);
    
    useEffect( () => {
        cargarProductos();
    }, []);

    function cargarProductos(){
        Promise.all([fetch("http://localhost:4000/obtProdDestacado", { method: "GET" }), 
                     fetch("http://localhost:4000/obtProductosPC", { method: "GET" }),
                     fetch("http://localhost:4000/obtProductosNot", { method: "GET" }),
                     fetch("http://localhost:4000/obtProductosCons", { method: "GET" })]).then(promesasResueltas => {
                        Promise.all(promesasResueltas.map(pR => pR.json()))
                      })
                      .then(([destacados, pcEscritorio, notebooks, consolas]) => {
                         cambiarDestacados(destacados.data);
                         cambiarPcsEscritorio(pcEscritorio.data);
                         cambiarNotebooks(notebooks.data);
                         cambiarConsolas(consolas.data);
                      });
    }

    return(
        <main id="main">
            <SectionArticulo 
                productos = {destacados} 
                estiloSection = {"destacado"} 
                idSection = {"destacados"} 
                titulo = {"Destacados"} 
                idDiv = {"divDestacado"}
            />
            <SectionArticulo 
                productos = {pcsEscritorio} 
                estiloSection = {"PC-Escritorio"} 
                idSection = {"PC-Escritorio"} 
                titulo = {"PC Escritorio"} 
                idDiv = {"divEscritorio"}
            />
            <SectionArticulo 
                productos = {notebooks} 
                estiloSection = {"Notebooks"} 
                idSection = {"Notebooks"} 
                titulo = {"Notebooks"} 
                idDiv = {"divNotebook"}
            />
            <SectionArticulo 
                productos = {consolas} 
                estiloSection = {"Consolas"} 
                idSection = {"Consolas"} 
                titulo = {"Consolas"} 
                idDiv = {"divConsolas"}
            />
        </main> 
    );
}

export default Tienda;
