import React, { useEffect } from "react";
import './comun.css';
import './tienda.css';
import ArticulosProductos from './articuloProducto';

function Tienda(){
    const destacados = [];
    const pcsEscritorio = [];
    const notebooks = [];
    const consolas = [];
    
    useEffect(() => {
        cargarProductos();
    }, []);

    async function cargarProductos(){
        const destacadosSinParsear =  await fetch("http://localhost:4000/obtProdDestacado", { method: "GET" });
        destacados = await destacadosSinParsear.json();
        
        const pcsEscritorioSinParsear =  await fetch("http://localhost:4000/obtProductosPC", { method: "GET" });
        pcsEscritorio = await pcsEscritorioSinParsear.json();

        const notebooksSinParsear =  await fetch("http://localhost:4000/obtProductosNot", { method: "GET" });
        notebooks = await notebooksSinParsear.json();

        const consolasSinParsear =  await fetch("http://localhost:4000/obtProductosCons", { method: "GET" });
        consolas = await consolasSinParsear.json();
    }

    return(
        <main id="main">
            <section className="destacado" id="destacados">
                <h2>Destacados</h2>
                <div id="divDestacado" className="media-q">
                    <ArticulosProductos productos = {destacados}/>
                </div>
            </section>
            <section className="PC-Escritorio" id="PC-Escritorio">
                <h2>PC Escritorio</h2>
                <div id="divEscritorio" className="media-q">

                </div>
            </section>
            <section className="Notebooks" id="Notebooks">
                <h2>Notebooks</h2>
                <div id="divNotebook" className="media-q">

                </div>
            </section>
            <section className="Consolas" id="Consolas">
                <h2>Consolas</h2>
                <div id="divConsolas" className="media-q">

                </div>
            </section>
        </main> 
    );
}

export default Tienda;
