                            fetchDestacados = resultado.data;
                        });

                        fetchPcsEscritorio.json().then(resultado => {
                            fetchPcsEscritorio = resultado.data;
                        });

                        fetchNotebooks.json().then(resultado => {
                            fetchNotebooks = resultado.data;
                        });

                        fetchConsolas.json().then(resultado => {
                            fetchConsolas = resultado.data;
                        });

                        cambiarDestacados(fetchDestacados.data);
                        cambiarPcsEscritorio(fetchPcsEscritorio.data);
