                                cambiarDestacados(destacados.data);
                                cambiarDestacados(pcsEscritorio.data);
                                cambiarDestacados(notebooks.data);
                                cambiarDestacados(consolas.data);
                             }
                         );*/
                         
                        /*values.map(function (value, i){
                            value.json().then(resultado => { 
                                if(i == 0){
                                    cambiarDestacados(resultado.data);
                                    console.log('destacados', destacados)
                                }
                                if(i == 1){
                                    cambiarPcsEscritorio(resultado.data);
                                    console.log('escritorio', pcsEscritorio)
                                }
                                if(i == 2){
                                    cambiarNotebooks(resultado.data);
                                    console.log('notebooks', notebooks)
                                }
                                if(i == 3){
                                    cambiarConsolas(resultado.data);
                                    console.log('consolas', consolas);
                                }
                            })
                        })*/
                    });
