            console.log(e);
            console.groupEnd();
        }*/
    }

    return(
        <main id="main">
            <SectionArticulo 
                productos = {destacados} 
                estiloSection = {"destacado"} 
                idSection = {"destacados"} 
                titulo = {"Destacados"} 
                idDiv = {"divDestacado"}
            />
            <SectionArticulo 
                productos = {pcsEscritorio} 
                estiloSection = {"PC-Escritorio"} 
                idSection = {"PC-Escritorio"} 
                titulo = {"PC Escritorio"} 
                idDiv = {"divEscritorio"}
            />
            <SectionArticulo 
                productos = {notebooks} 
                estiloSection = {"Notebooks"} 
                idSection = {"Notebooks"} 
                titulo = {"Notebooks"} 
                idDiv = {"divNotebook"}
            />
            <SectionArticulo 
                productos = {consolas} 
                estiloSection = {"Consolas"} 
                idSection = {"Consolas"} 
                titulo = {"Consolas"} 
                idDiv = {"divConsolas"}
            />
        </main> 
    );
}

export default Tienda;
