import React, { useEffect, useState } from "react";
import 'mapbox-gl/dist/mapbox-gl.css';
import './mapa.css';
//import mapboxgl from 'mapbox-gl';
import L from "leaflet";
import "leaflet-routing-machine";

function Mapa({ sucursal }) {
    /*const [lat, setLat] = useState(0);
    const [lng, setLng] = useState(0);
    const [direccion, setDireccion] = useState('');*/
    const [mipos, setMipos] = useState({});
    let mymap = "";

    useEffect( () => {
        /*cargarVariables();
        cargarMapa();*/
        mymap = new L.map("map");
        navigator.geolocation.getCurrentPosition(success, error);
    }, []);

    /*function cargarVariables(){
        if(sucursal === 'Sucursal 1'){
            setLat(-34.883817);
            setLng(-56.171343);
            setDireccion('Juan Paullier 2378 entre Amézaga y Domingo Aramburú');
        }

        if(sucursal === 'Sucursal 2'){
            setLat(-34.898653);
            setLng(-56.138224);
            setDireccion('Demóstenes 3532');
        }
    }

    function cargarMapa(){
        mapboxgl.accessToken = 'pk.eyJ1IjoianBvcnRlaXJvIiwiYSI6ImNrbm54MzV4eTEyaXoyc294Y3F3bXV0d3AifQ.MLAbktKm3nrID_oLqs1B8g';
        const map = new mapboxgl.Map({
            container: 'map', // container ID
            style: 'mapbox://styles/mapbox/streets-v11', // style URL
            center: [lng, lat], // starting position [lng, lat]
            zoom: 16 // starting zoom
        });

        new mapboxgl.Marker({
            color: "black",
            draggable: false
            }).setLngLat([lng, lat])
            .setPopup(new mapboxgl.Popup().setHTML(sucursal))
            .addTo(map);
        
            map.addControl(new mapboxgl.NavigationControl());
            map.addControl(new mapboxgl.FullscreenControl());
    }*/

    
  
    /* Con esta función, en caso de que pudimos obtener correctamente la ubicación actual del usuario, dibuja el mapa con
        la posición actual del usuario y la posición de la sucursal, y dibuja un camino entre ambos puntos */
    function success(pos) {
        setMipos({lat: pos.coords.latitude, lon: pos.coords.longitude});

        /* Se instancia un nuevo mapa */
        mymap.setView([mipos.lat, mipos.lon], 15);

        L.tileLayer(
            "https://api.mapbox.com/styles/v1/{id}/tiles/{z}/{x}/{y}?access_token=pk.eyJ1IjoianBvcnRlaXJvIiwiYSI6ImNrbm54MzV4eTEyaXoyc294Y3F3bXV0d3AifQ.MLAbktKm3nrID_oLqs1B8g",
            {
            attribution:
                'Map data &copy; <a href="https://www.openstreetmap.org/">OpenStreetMap</a> contributors, <a href="https://creativecommons.org/licenses/by-sa/2.0/">CC-BY-SA</a>, Imagery © <a href="https://www.mapbox.com/">Mapbox</a>',
            maxZoom: 18,
            id: "mapbox/streets-v11",
            accessToken: "your.mapbox.access.token"
            }
        ).addTo(mymap);

        L.marker([mipos.lat, mipos.lon])
            .bindPopup('YO')
            .addTo(mymap);

        /* Se crea la ruta entre ambas ubicaciones */
        L.Routing.control({
            waypoints: [
                L.latLng(mipos.lat, mipos.lon),
                L.latLng(-34.883817, -56.171343)
            ]
        }).addTo(mymap);
    }

    /* Con esta función, en caso de que NO pudimos obtener correctamente la ubicación actual del usuario, dibuja el mapa con
        la posición de la sucursal */
    function error() {

        /* Se instancia un nuevo mapa */
        mymap.setView([-34.883817, -56.171343], 15);

        L.tileLayer(
            "https://api.mapbox.com/styles/v1/{id}/tiles/{z}/{x}/{y}?access_token=pk.eyJ1IjoianBvcnRlaXJvIiwiYSI6ImNrbm54MzV4eTEyaXoyc294Y3F3bXV0d3AifQ.MLAbktKm3nrID_oLqs1B8g",
            {
            attribution:
                'Map data &copy; <a href="https://www.openstreetmap.org/">OpenStreetMap</a> contributors, <a href="https://creativecommons.org/licenses/by-sa/2.0/">CC-BY-SA</a>, Imagery © <a href="https://www.mapbox.com/">Mapbox</a>',
            maxZoom: 18,
            id: "mapbox/streets-v11",
            accessToken: "your.mapbox.access.token"
            }
        ).addTo(mymap);

        /* Se marca la posición de la sucursal */
        L.marker([-34.883817, -56.171343])
            .bindPopup('Sucursal 1')
            .addTo(mymap);
    }

    return(
        /*<Map center={0} zoom={15} ref={this.saveMap}>
            <TileLayer
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
            attribution='&copy; <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
            />
            {this.state.isMapInit && <Routing map={this.map} />}
        </Map>*/

        <main id="main">
            
                <div id="map"></div>
                <div className="horario">
                    <p>Nuestro horario de atención es:</p>
                    <ul>
                        <li>De Lunes a Viernes de 9:00 a 13 y 14 a 19 hrs</li>
                        <li>Los Sábados de  9 a 14hrs</li>
                    </ul>
                </div>
                <div className="ubicacion">
                    <p>Dirección:</p>
                    <ul>
                        <li>{/*direccion*/}</li>
                    </ul>
                </div>
            
        </main>
    );
}
  
export default Mapa;
