import React, { useEffect, useState } from "react";
import 'mapbox-gl/dist/mapbox-gl.css';
import './mapa.css';
import mapboxgl from 'mapbox-gl';

function Mapa(props) {
    console.log('Estoy en Mapa')
    const [lat, setLat] = useState(0);
    const [lng, setLng] = useState(0);
    const [direccion, setDireccion] = useState('');
    const [sucursal, setSucursal] = useState('');

    useEffect( async () => {
        cargarVariables();
        await cargarMapa();
    }, []);

    function cargarVariables(){
        if(props){
            setSucursal(props.sucursal);
        }
    
        if(sucursal === 'Sucursal 1'){
            setLat(-34.883817);
            setLng(-56.171343);
            setDireccion('Juan Paullier 2378 entre Amézaga y Domingo Aramburú');
        }
    
        if(sucursal === 'Sucursal 2'){
            setLat(-34.898653);
            setLng(-56.138224);
            setDireccion('Demóstenes 3532');
        }

        console.log('sucursal', sucursal);
    console.log('lat', lat);
    console.log('lng', lng);
    console.log('direccion', direccion);
    }

    /*console.log('sucursal', sucursal);
    console.log('lat', lat);
    console.log('lng', lng);
    console.log('direccion', direccion);*/

    function cargarMapa(){
        mapboxgl.accessToken = 'pk.eyJ1IjoianBvcnRlaXJvIiwiYSI6ImNrbm54MzV4eTEyaXoyc294Y3F3bXV0d3AifQ.MLAbktKm3nrID_oLqs1B8g';
        const map = new mapboxgl.Map({
            container: 'map', // container ID
            style: 'mapbox://styles/mapbox/streets-v11', // style URL
            center: [lng, lat], // starting position [lng, lat]
            zoom: 16 // starting zoom
        });

        new mapboxgl.Marker({
            color: "black",
            draggable: false
            }).setLngLat([lng, lat])
            .setPopup(new mapboxgl.Popup().setHTML(sucursal))
            .addTo(map);
        
            map.addControl(new mapboxgl.NavigationControl());
            map.addControl(new mapboxgl.FullscreenControl());
    }
 
    return(
        <main id="main">
            <div id="contenedor">
                <div id="map"></div>
                <div className="horario">
                    <p>Nuestro horario de atención es:</p>
                    <ul>
                        <li>De Lunes a Viernes de 9:00 a 13 y 14 a 19 hrs</li>
                        <li>Los Sábados de  9 a 14hrs</li>
                    </ul>
                </div>
                <div className="ubicacion">
                    <p>Dirección:</p>
                    <ul>
                        <li>{direccion}</li>
                    </ul>
                </div>
            </div>
        </main>
    );
}
  
export default Mapa;
