import React, { useState, useEffect } from "react";
//import './comun.css';
import './tienda.css';
import SectionArticulo from './sectionArticulo';

function Tienda(){
    const [destacados, cambiarDestacados] = useState([]);
    const [pcsEscritorio, cambiarPcsEscritorio] = useState([]);
    const [notebooks, cambiarNotebooks] = useState([]);
    const [consolas, cambiarConsolas] = useState([]);
    
    useEffect( () => {
        cargarProductos();
    }, []);

    async function cargarProductos(){
        Promise.all([fetch("http://localhost:4000/obtProdDestacado", { method: "GET" }), 
                     fetch("http://localhost:4000/obtProductosPC", { method: "GET" }),
                     fetch("http://localhost:4000/obtProductosNot", { method: "GET" }),
                     fetch("http://localhost:4000/obtProductosCons", { method: "GET" })]).then(values => {
                        values.map(function (value, i){
                            value.json().then(resultado => { 
                                if(i == 0){
                                    cambiarDestacados(resultado.data);
                                }
                                if(i == 1){
                                    cambiarPcsEscritorio(resultado.data);
                                }
                                if(i == 2){
                                    cambiarNotebooks(resultado.data);
                                }
                                if(i == 3){
                                    cambiarConsolas(resultado.data);
                                }
                            })
                        })
                    });
