import React, { useState, useEffect } from "react";
import './comun.css';
import './tienda.css';
import Sectionarticulo from './sectionArticulo';

function Tienda(){
    const [destacados, cambiarDestacados] = useState([]);
    const [pcsEscritorio, cambiarPcsEscritorio] = useState([]);
    const [notebooks, cambiarNotebooks] = useState([]);
    const [consolas, cambiarConsolas] = useState([]);
    
    useEffect( () => {
        cargarProductos();
    }, []);

    async function cargarProductos(){
        try{
            const destacadosSinParsear =  await fetch("http://localhost:4000/obtProdDestacado", { method: "GET" });
            const parseResult = await destacadosSinParsear.json();
            cambiarDestacados(parseResult.data);
            console.log('destacados', destacados);
        }catch(e){
            console.group('Excepción');
            console.log("Error al obtener los productos destacados")
            console.log(e);
            console.groupEnd();
        }
        
        try{
            const pcsEscritorioSinParsear =  await fetch("http://localhost:4000/obtProductosPC", { method: "GET" });
            const parseResult = await pcsEscritorioSinParsear.json();
            cambiarPcsEscritorio(parseResult.data);
        }catch(e){
            console.group('Excepción');
            console.log("Error al obtener los productos PC de escritorio")
            console.log(e);
            console.groupEnd();
        }
        
        try{
            const notebooksSinParsear =  await fetch("http://localhost:4000/obtProductosNot", { method: "GET" });
            const parseResult = await notebooksSinParsear.json();
            cambiarNotebooks(parseResult.data);
        }catch(e){
            console.group('Excepción');
            console.log("Error al obtener los productos NoteBooks")
            console.log(e);
            console.groupEnd();
        }

        try{
            const consolasSinParsear =  await fetch("http://localhost:4000/obtProductosCons", { method: "GET" });
            const parseResult = await consolasSinParsear.json();
            cambiarConsolas(parseResult.data);
        }catch(e){
            console.group('Excepción');
            console.log("Error al obtener los productos Consolas")
            console.log(e);
            console.groupEnd();
        }
    }

    return(
        <main id="main">
            <Sectionarticulo 
                productos = {destacados} 
                estiloSection = {"destacado"} 
                idSection = {"destacados"} 
                titulo = {"Destacados"} 
                idDiv = {"divDestacado"}
            />
            <Sectionarticulo 
                productos = {pcsEscritorio} 
                estiloSection = {"PC-Escritorio"} 
                idSection = {"PC-Escritorio"} 
                titulo = {"PC Escritorio"} 
                idDiv = {"divEscritorio"}
            />
            <Sectionarticulo 
                productos = {notebooks} 
                estiloSection = {"Notebooks"} 
                idSection = {"Notebooks"} 
                titulo = {"Notebooks"} 
                idDiv = {"divNotebook"}
            />
            <Sectionarticulo 
                productos = {consolas} 
                estiloSection = {"Consolas"} 
                idSection = {"Consolas"} 
                titulo = {"Consolas"} 
                idDiv = {"divConsolas"}
            />
        </main> 
    );
}

export default Tienda;
