import React, { useEffect, useState } from 'react';
import {
    useParams
} from "react-router-dom";
import './comun.css';
import './botones.css';
import './inicio.css';

function DetalleProducto(){
    let { idProducto } = useParams();
    const [producto, cambiarProducto] = useState([]);

    useEffect( () => {
        if(!idProducto) return <div>No hay productos</div>;

        Promise.all([
            fetch(`http://localhost:4000/obtProducto/?id=${idProducto}`, { method: "GET" })
        ])
        .then(promesaResuelta => {
            Promise.all(promesaResuelta.map(pR => pR.json()))
            .then(([resultadoProducto]) => {
                console.log(resultadoProducto)
                cambiarProducto(resultadoProducto.data);
            })
            console.log(producto)
        })
        .catch(err => {
            return(
                <div>
                    No se encuentra el producto seleccionado. Vuelva a cargar la p√°gina
                </div>
            );
        });
    }, [idProducto]);

    return(
        <main id="main">
            <div>{producto.id}</div>
            <div>{producto.precio}</div>
            <div>{producto.descripcion}</div>
            <div>{producto.imagen}</div>
        </main>
    );
}

export default DetalleProducto;
